\begin{table}[h]
    \centering
    \caption{城市间距离矩阵（表 7-24）}
    \renewcommand{\arraystretch}{1.2}
    \begin{tabular}{c|cccc}
        \toprule
        距离 $d(v_i, v_j)$ & 1 & 2 & 3 & 4 \\
        \midrule
        1 & 0 & 6 & 7 & 9 \\
        2 & 8 & 0 & 9 & 7 \\
        3 & 5 & 8 & 0 & 8 \\
        4 & 6 & 5 & 5 & 0 \\
        \bottomrule
    \end{tabular}
\end{table}
